import { useState } from "react";
import BlogList from "./BlogList";

const Home = () => {

    const [blogs, setBlogs] = useState([
        {title: 'My new website', body: 'meep meep meep', author: 'mario', id: 1},
        {title: 'My second website', body: 'beep beep beep', author: 'wario', id: 2},
        {title: 'My third website', body: 'reep reep mreep', author: 'doodoo', id: 3},
    ]);

    const handleDelete = (id) => {
        let newBlogs = blogs.filter((blog) => blog.id !== id);
        setBlogs(newBlogs);
    }

    return (  
        <div className="home">
          <BlogList blogs={blogs} title='All Blogs' handleDelete={handleDelete}/>
          <BlogList blogs={blogs.filter((blog) => blog.id % 2 !== 0)} title='Odd # ID Blogs' handleDelete={handleDelete}/>
        </div>
    );
}

export default Home;
